package br.com.solutis.main;

import br.com.solutis.entity.Block;
import org.springframework.stereotype.Component;

@Component
public class Application {

	Application() {
	}

	private Blockchain blockchain;

	public void startBlockchain() {
		blockchain = new Blockchain();
		blockchain.addBlock(new Block("Data1"));
		blockchain.addBlock(new Block("Data2"));
		blockchain.addBlock(new Block("Data3"));
		blockchain.addBlock(new Block("Data4"));
	}

	public void printBlockchain() {
		for (Block block : blockchain.getChain()) {
			System.out.println(block);
		}
	}

	public void showWelcomeMsg() {
		String welcome = "";
		System.out.println(this.hexToAscii(welcome));
	}

	private String hexToAscii(String hexStr) {
		StringBuilder output = new StringBuilder("");

		for (int i = 0; i < hexStr.length(); i += 2) {
			String str = hexStr.substring(i, i + 2);
			output.append((char) Integer.parseInt(str, 16));
		}

		return output.toString();
	}
}
